mapper.select:
SELECT *
FROM MiniHP_Visitor
WHERE OwnerID = #{OwnerID} AND GuestID = #{GuestID}

SELECT hpToday
FROM MiniHP_Intro
WHERE UserID = #{UserID}

mapper.update:
UPDATE MiniHP_Intro
SET hpToday = hpToday + 1
WHERE UserID = #{UserID}

mapper.select:
SELECT hpTotal
FROM MiniHP_Intro
WHERE UserID = #{UserID}

mapper.update:
UPDATE MiniHP_Intro
SET hpTotal = hpTotal + 1
WHERE UserID = #{UserID}

mapper.select:
SELECT mi.hpTitle, hu.UserID, hu.UserName 
FROM MiniHP_Intro mi, HW_USERLIST hu
WHERE mi.UserSerial = hu.UserSerial;
