mapper.update:
UPDATE UserSelect_Article
SET backgroundSrc = #{storyRoom}
WHERE UserSerial = #{UserSerial}

UPDATE UserSelect_Article
SET characterSrc = #{minimi}, minimiX = #{minimiX}, minimiY = #{minimiY}
WHERE UserSerial = #{UserSerial}

UPDATE UserSelect_Article
SET mouseSrc = #{mousePointer}
WHERE UserSerial = #{UserSerial}
